import React, { useState } from 'react';
import './App.css';
import Todo from './Components/Taskboard.js';

function App() {
 const [tasks, setTasks] = useState([{id: '', taskname: '', status: ''}]);
 const [count, setCount] = useState(0);
 const [currStatus, setCurrStatus] = useState('');
 const [taskname, setTaskname] = useState('');

function handleSubmit(e){
  e.preventDefault();

  if(taskname){
      setCount(count+1);
      setTasks(...tasks, {id: count, taskname: taskname,  status: 'active'});
  }
}

  return (
    <div className="App">
      <div className='main'>
      <article className='border shadow w-max-4xl w-6/12'>
        <header className='w-max-4xl flex flex-col border px-20 py-10'>
          <div className='row'>
            <form className='flex flex-row'>
              <input type="text" className='border border-black-300 p-3 w-10/12' value = {taskname} onchange={(e) => { setTaskname(e.target.value)}} placeholder='What to do...'></input>
              <button className='w-2/12 bg-blue-500 text-white duration-300 hover:bg-blue-600' onSubmit={handleSubmit}>Add Task</button>
            </form>

          </div>
        <div className='row m-2 px-5'>
          <span className='flex flex-row space-x-4'>
            <button aria-pressed="true" className='w-2/12 rounded-full p-3 border border-gray-200 duration-200 text-black hover:bg-orange-400 hover:border-orange-400 hover:text-white' onClick={() => {setCurrStatus('active')}}>Active</button>
            <button  aria-pressed="true" className='w-2/12 rounded-full p-3 border border-gray-200 duration-200 text-black hover:bg-green-400 hover:border-green-400 hover:text-white'>Completed</button>
            <button  aria-pressed="true" className='w-2/12 rounded-full p-3 border border-gray-200 duration-200 text-black hover:bg-blue-400 hover:border-blue-400 hover:text-white'>ALL</button>
          </span>
        </div>
        </header>
        <ul className='container' aria-labelledby="list-heading">
          { tasks.map((task) => {
            if(task.status = currStatus){
              <Todo name={task.taskname} id={task.id} status={task.status} />
            }
          }) }
        </ul>
      </article>
      </div>

    </div>
  );
}

export default App;
