        </div>
        </header>
        <ul className='container' aria-labelledby="list-heading">
          <Todo name="Eat" />
          <Todo name="Sleep"/>
          <Todo name="Repeat" />
        </ul>
      </article>
      </div>

    </div>
  );
}

export default App;
